# 1. Number of elements (possible methods: size, length, count)
def number_of_elements(data)
	number = data.length
	puts "The number of handles is #{number}"
	return number
end

# 2. Shortest element in the list
# Technique gourmande en mémoire (création d'une nouelle array)
def shortest_element(data)
	#& Opérateur de navigation sécurisée, il permet d’appeler des méthodes sur des objets sans s’inquiéter du fait que cet objet peut être nil (éviter une erreur undefined method for nil:NilClass), similaire à la méthode try dans Rails
	shortest = data.sort_by(&:length)[0] 
	puts "the shortest handle is #{shortest}"
	return shortest
end

# 3. Combien de handles ont 5 caratères hors @ ?
def number_of_5_characters(data)
count_5 = 0
data.each do |i|
	if i.length == 6 # target +1 à cause du @
		count_5 += 1
	end
end
puts "le nombre d'handles ayant 5 caractères est #{count_5}"
return count_5
end

	# 4. Combien de handles commencent-ils avec une majuscule ?
def number_of_capitalized_handles(data)
	count_upper = 0
	data.each do |i|
		if i[1] == i[1].upcase
			count_upper += 1
		end
	end
	puts "le nombre d'handles ayant une majuscule est #{count_upper}"
	return count_upper
	end

# 5. Trier la liste par ordre alphabétique
def ordre_alpha(data)
	#sorted_by_alpha = [] On est pas en C, c'est pas nécessaire
	sorted_by_alpha = data.sort_by(&:downcase)
	#puts sorted_by_alpha
	# return ?
end

# 6. Trie la liste de handles par taille des handle (ordre croissant) VOIR 2
def sort_by_length(data)
	sorted_by_length = data.sort_by(&:length)
	# puts sorted_by_length
	return sorted_by_length
end

# 7. position de l'array pour l'handle @epenser ?
def spot_specific_handle(data)
data.each do |i|
	if i.to_s == "@epenser"
		puts data.index(i)
	end
end
end

# 8. Répartition des handles par taille (ordre croissant)
# Array d'arrays [][]?
def number_of_handles_per_given_length(data)
	#puts "on récupère bien #{sort_by_length(data)}"
	
end

def perform(data)
	number_of_elements(data) #1
	shortest_element(data) #2
	number_of_5_characters(data) #3
	number_of_capitalized_handles(data) #4 
	ordre_alpha(data) #5
	sort_share = sort_by_length(data) #6
	sort_by_length(data) 
	spot_specific_handle(data) #7
	number_of_handles_per_given_length(sort_share) #8
end 

